import { useEffect } from "react";
import styles from "./FabricBoard.module.css";
import { fabric } from "fabric";

export default function FabricBoard() {
  useEffect(() => {
    const wrapperElem = document.getElementById("canvas-wrapper");
    var canvas = new fabric.Canvas("c", {
      width: wrapperElem.clientWidth,
      height: wrapperElem.clientHeight,
      isDrawingMode: true,
    });
    const canvasElem = document.getElementById("c");
    canvasElem.width = document.body.clientWidth; //document.width is obsolete
    canvasElem.height = document.body.clientHeight;
    const ctx = canvasElem.getContext("2d");
    ctx.fillStyle = "white";
  }, []);

  return (
    <div className={styles.wrapper}>
      <button>Clear</button>
      <div id="canvas-wrapper">
        <canvas id="c"></canvas>
      </div>
    </div>
  );
}
